package chapter2;

public class BinaryTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10; 
		int bNum = 0B1010; // 2진수
		int oNum = 012; // 8진수
		int hNum = 0XA; // 16진수
		
		System.out.println(num);
		System.out.println(bNum);
		System.out.println(oNum);
		System.out.println(hNum);
		
		// 모두 10을 출력하고 있다. 
	}

}
