package loopexample;

public class ForExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num;
		int sum = 0;
		
		for(num = 1; num <=10; num++) {
			sum+=num;
		}
		
		System.out.println("1부터 10까지의 합은 "+sum+"입니다.");
		System.out.println(num);
		
	}

}
